import React, { useState } from 'react';
import HomeOver from '../components/HomeOver';
import { Container, Button } from 'reactstrap';
import './Home.css';
import { BrowserRouter as Router, Route, Switch, Link } from "react-router-dom";
import FromHome from '../views/FromHome';
import FromSodimac from '../views/FromSodimac';

const Home = (props) => {

    const [loading, setLoading] = useState(true);

    setTimeout( () => {
        setLoading(false);
    },3200);

    return (
        <div className="home col-md-12">
            <Router>
                <Switch>
                    <Route path="/" exact component={ Home } />
                    <Route path="/home" component={ FromHome } />
                    <Route path="/index" component={ FromSodimac } />
                    {/* redirect user to SignIn page if route does not exist and user is not authenticated */}
                    <Route component={ Home } />
                </Switch>
            </Router>

            { loading
                ?
                    <Container>
                        <HomeOver />
                    </Container>
                :
                    <div className="options">
                        <h2>¿Dónde te encuentras?</h2>
                        <p>te ayudaremos a encontrar los productos que necesitas</p>
                        <Link to="/index"><Button color="primary" className="buttons">EN SODIMAC</Button></Link>            
                        <Link to="/home"><Button color="primary" className="buttons">EN MI HOGAR</Button></Link>
                    </div>
            }
        </div>
    );
};

export default Home;
